sys_ui_page.client_script:
showDatePicker();
function showDatePicker(){
	var cbox = gel("need_date_range").checked;
	var dpick = gel("date_pick");
	if (cbox) {
		dpick.style.display = "block";
	} else {
		dpick.style.display = "none";
	}
}
